.hero {
  max-width: 90%;
  height: 95vh;
  margin: 0 auto;
  &__name{
    animation: fill .5s ease forwards 3.5s;
  }
  &__about {
    left: 50%;
    position: absolute;
    transform: translate(-50%, -50%);
    top: 50%;
    @include flex-col;
    gap: 3rem;
    text-align: justify;
    svg{
      width: 80%;
      height: 100%;
    }
    h2 {
      font-family: "Montserrat Subrayada", sans-serif;
      font-size: 5rem;
      min-width: max-content;
      animation: drop-down .5s 2s forwards;
      opacity: 0;
    }
    p {
      max-width: 90rem;
      font-size: inherit;
      line-height: 1.5;
      opacity: 0;
      animation: display-text 2s 3s forwards;
      svg {
        height: 1rem;
        margin-left: 2rem;
      }
    }
  }
  &__image {
    width: 90%;
    img {
      width: 100%;
    }
  }
  .down-arrow{
    position: absolute;
    padding: 1rem;
    bottom: -10%;
    left: 50%;
    transform: translateX(-50%);
    opacity: 0;
    position: absolute;
    display: inline-block;
    animation: arrow 2s 4s infinite;
  }
}

